function setCookie(e,o,t){const n=new Date;n.setTime(n.getTime()+24*t*60*60*1e3);let i="expires="+n.toUTCString();document.cookie=`${e}=${o}; ${i}; path=/`}function deleteCookie(e){setCookie(e,null,null)}function getCookie(e){const o=decodeURIComponent(document.cookie).split("; ");let t=null;return o.forEach(o=>{0==o.indexOf(e)&&(t=o.substring(e.length+1))}),t}console.log("Credits to Bro Code for the cookie system code") // credits bro code on yt
